mapper.insert:
/**  com.easycompany.mapper.EduMapper.insertSchedule **/	

	SELECT IFNULL(MAX(EDU_KEY), 0) + 1 
	FROM EDU_SCHEDULE			  


INSERT 
INTO EDU_SCHEDULE 
(     EDU_KEY
  	, CATEGORY1_KEY
  	, CATEGORY2_KEY
  	, CATEGORY3_KEY 
  	, CATEGORY1_NAME
  	, CATEGORY2_NAME
  	, CATEGORY3_NAME
  	, EDU_NAME
  	, EDU_START_DATE
  	, EDU_END_DATE
  	, EDU_ORG_NAME
  	, EDU_TARGET
  	, EDU_NUMBER
  	, EDU_TEAC_NAME
  	, REG_ID
) 
VALUES 
(
	  #{edu_key}
	, #{category1_key}
	, #{category2_key}
	, #{category3_key}
	, #{category1_name}
	, #{category2_name}
	, #{category3_name}
	, #{edu_name}
	, #{edu_start_date}
	, #{edu_end_date}
	, #{edu_org_name}
	, #{edu_target}
	, #{edu_number}
	, #{edu_teac_name}
	, IFNULL(#{reg_id},'SYSTEM')
)

mapper.update:
		/**  com.easycompany.mapper.EduMapper.updateSchedule **/	
		UPDATE  EDU_SCHEDULE 
		   SET  CATEGORY1_KEY   = #{category1_key}       
			  , CATEGORY2_KEY   = #{category2_key} 
			  , CATEGORY3_KEY   = #{category3_key} 
			  , CATEGORY1_NAME  = #{category1_name}
			  , CATEGORY2_NAME  = #{category2_name}
			  , CATEGORY3_NAME  = #{category3_name}
			  , EDU_NAME        = #{edu_name}      
			  , EDU_START_DATE  = #{edu_start_date}
			  , EDU_END_DATE    = #{edu_end_date}  
			  , EDU_ORG_NAME    = #{edu_org_name}  
			  , EDU_TARGET      = #{edu_target}    
			  , EDU_NUMBER      = #{edu_number}    
			  , EDU_TEAC_NAME   = #{edu_teac_name} 
		      , CHG_ID = IFNULL(#{reg_id},'SYSTEM')
		      , CHG_DT = NOW()
 		 WHERE EDU_KEY = #{edu_key}

mapper.select:
/** com.easycompany.mapper.EduMapper.eduInfoScheduleList **/ WITH TEMP AS ( SELECT a.CATEGORY1_KEY ,(SELECT b.CATEGORY1_NAME FROM EDU_CATEGORY1 b WHERE b.CATEGORY1_KEY = a.CATEGORY1_KEY ) CATEGORY1_NAME ,a.CATEGORY2_KEY ,(SELECT b.CATEGORY2_NAME FROM EDU_CATEGORY2 b WHERE b.CATEGORY1_KEY = a.CATEGORY1_KEY AND b.CATEGORY2_KEY = a.CATEGORY2_KEY ) CATEGORY2_NAME , a.CATEGORY3_KEY ,(SELECT b.CATEGORY3_NAME FROM EDU_CATEGORY3 b WHERE b.CATEGORY1_KEY = a.CATEGORY1_KEY AND b.CATEGORY2_KEY = a.CATEGORY2_KEY AND b.CATEGORY3_KEY = a.CATEGORY3_KEY ) CATEGORY3_NAME , EDU_NAME , EDU_START_DATE , EDU_END_DATE , EDU_ORG_NAME , EDU_TARGET , EDU_NUMBER , EDU_TEAC_NAME , EDU_KEY FROM EDU_SCHEDULE A ) SELECT * FROM TEMP WHERE 1= 1
AMD CATEGORY3_NAME LIKE CONCAT('%', #{category3_name} ,'%')
AND EDU_START_DATE = DATE_FORMAT(NOW(),'%x-%m-%d')
AND EDU_START_DATE >= #{edu_start_date} AND EDU_END_DATE <= #{edu_end_date}
ORDER BY CATEGORY1_KEY LIMIT #{recordCountPerPage} OFFSET #{offset}

    	/**  com.easycompany.mapper.EduMapper.eduInfoScheduleCount **/
    	SELECT COUNT(*) cnt
		 FROM EDU_SCHEDULE a
