mapper.select:
		SELECT  (SELECT CATEGORY1_NAME FROM EDU_CLASS_CATEGORY1 WHERE CATEGORY1_KEY = b.CATEGORY1_KEY) AS CATEGORY1_NAME
				,(SELECT CATEGORY2_NAME FROM EDU_CLASS_CATEGORY2 WHERE CATEGORY2_KEY = b.CATEGORY2_KEY) AS CATEGORY2_NAME
		        ,(SELECT CATEGORY3_NAME FROM EDU_CLASS_CATEGORY3 WHERE CATEGORY3_KEY = b.CATEGORY3_KEY) AS CATEGORY3_NAME
		        ,DATE_FORMAT(a.REG_DT,'%Y-%m-%d') AS REG_DT      
		FROM EDU_COURSE_REGISTRATION a, 
			 EDU_REGISTRATION b
		WHERE a.EDU_NO = b.EDU_NO
		AND a.USER_ID = #{UserAccount.user_id}

        AND TRAIN_S_DATE > NOW()  


			AND b.CATEGORY1_KEY = #{category1_key}


			AND b.CATEGORY2_KEY = #{category2_key}


			AND b.CATEGORY3_KEY = #{category3_key}





				AND REG_DT = NOW()



		       AND  REG_DT >= #{start_date} AND REG_DT <= #{end_date} 



		ORDER BY COUR_NO DESC
		LIMIT #{recordCountPerPage} OFFSET #{offset}

		SELECT COUNT(*) 
		 FROM EDU_COURSE_REGISTRATION a, 
			 EDU_REGISTRATION b
		WHERE a.EDU_NO = b.EDU_NO
		AND a.USER_ID = #{UserAccount.user_id}

        AND TRAIN_S_DATE > NOW() 


			AND b.CATEGORY1_KEY = #{category1_key}


			AND b.CATEGORY2_KEY = #{category2_key}


			AND b.CATEGORY3_KEY = #{category3_key}





				AND REG_DT = NOW()



		       AND  REG_DT >= #{start_date} AND REG_DT <= #{end_date}

SELECT  COUNT(c.COUR_NO) AS ALL_CNT,
		COUNT(CASE WHEN COUR_STAT=1 THEN 1 END) AS END_CNT,
        COUNT(CASE WHEN COUR_STAT!=1 THEN 1 END) AS ING_CNT
FROM (
	SELECT  a.COUR_NO
	,CASE a.COUR_STAT WHEN 1 THEN '완료' ELSE '미완료' END AS COUR_STAT      
	FROM EDU_COURSE_REGISTRATION a, 
		 EDU_REGISTRATION b
	WHERE a.EDU_NO = b.EDU_NO
	AND a.USER_ID = #{UserAccount.user_id}

	AND TRAIN_S_DATE <= NOW() 


		AND b.CATEGORY1_KEY = #{category1_key}


		AND b.CATEGORY2_KEY = #{category2_key}


		AND b.CATEGORY3_KEY = #{category3_key}





			AND REG_DT = NOW()



	       AND  REG_DT >= #{start_date} AND REG_DT <= #{end_date} 



	) c

SELECT  (SELECT CATEGORY1_NAME FROM EDU_CLASS_CATEGORY1 WHERE CATEGORY1_KEY = b.CATEGORY1_KEY) AS CATEGORY1_NAME
		,(SELECT CATEGORY2_NAME FROM EDU_CLASS_CATEGORY2 WHERE CATEGORY2_KEY = b.CATEGORY2_KEY) AS CATEGORY2_NAME
        ,(SELECT CATEGORY3_NAME FROM EDU_CLASS_CATEGORY3 WHERE CATEGORY3_KEY = b.CATEGORY3_KEY) AS CATEGORY3_NAME
        ,DATE_FORMAT(a.REG_DT,'%Y-%m-%d') AS REG_DT
        ,a.USER_NM, a.COUR_NO
        ,CASE a.COUR_STAT
	 		WHEN 1 THEN '완료'
	 		ELSE '미완료'
		 END AS COUR_STAT         
FROM EDU_COURSE_REGISTRATION a, 
	 EDU_REGISTRATION b
WHERE a.EDU_NO = b.EDU_NO
AND a.USER_ID = #{UserAccount.user_id}

AND TRAIN_S_DATE <= NOW() 


	AND COUR_STAT = #{edu_status}


	AND b.CATEGORY1_KEY = #{category1_key}


	AND b.CATEGORY2_KEY = #{category2_key}


	AND b.CATEGORY3_KEY = #{category3_key}





		AND REG_DT = NOW()



       AND  REG_DT >= #{start_date} AND REG_DT <= #{end_date} 



ORDER BY COUR_NO DESC
LIMIT #{recordCountPerPage} OFFSET #{offset}

		SELECT COUNT(*) 
		 FROM EDU_COURSE_REGISTRATION a, 
			 EDU_REGISTRATION b
		WHERE a.EDU_NO = b.EDU_NO
		AND a.USER_ID = #{UserAccount.user_id}

        AND TRAIN_S_DATE <= NOW() 


			AND COUR_STAT = 1


			AND b.CATEGORY1_KEY = #{category1_key}


			AND b.CATEGORY2_KEY = #{category2_key}


			AND b.CATEGORY3_KEY = #{category3_key}





				AND REG_DT = NOW()



		       AND  REG_DT >= #{start_date} AND REG_DT <= #{end_date}

SELECT (SELECT CATEGORY1_NAME FROM EDU_CLASS_CATEGORY1 WHERE CATEGORY1_KEY = b.CATEGORY1_KEY) AS CATEGORY1_NAME ,(SELECT CATEGORY2_NAME FROM EDU_CLASS_CATEGORY2 WHERE CATEGORY2_KEY = b.CATEGORY2_KEY) AS CATEGORY2_NAME ,(SELECT CATEGORY3_NAME FROM EDU_CLASS_CATEGORY3 WHERE CATEGORY3_KEY = b.CATEGORY3_KEY) AS CATEGORY3_NAME ,DATE_FORMAT(a.REG_DT,'%Y-%m-%d') AS REG_DT, b.INST_NM, b.EDU_TIME ,IFNULL(TIME_FORMAT(SEC_TO_TIME(FLOOR(a.COUR_TIME)),'%i'),'00') AS COUR_TIME_MIN ,IFNULL(TIME_FORMAT(SEC_TO_TIME(FLOOR(a.COUR_TIME)),'%s'),'00') AS COUR_TIME_SEC ,a.USER_NM, a.COUR_NO, b.EDU_NO ,CASE a.COUR_STAT WHEN 1 THEN '완료' ELSE '미완료' END AS COUR_STAT FROM EDU_COURSE_REGISTRATION a, EDU_REGISTRATION b WHERE a.EDU_NO = b.EDU_NO AND a.USER_ID = #{UserAccount.user_id}
AND TRAIN_S_DATE <= NOW()
AND (a.COUR_STAT != '1' OR a.COUR_STAT IS NULL)
AND REG_DT = NOW()
AND REG_DT >= #{start_date} AND REG_DT <= #{end_date}
AND b.CATEGORY3_NAME = #{edu_name}
ORDER BY COUR_NO DESC

SELECT (SELECT CATEGORY1_NAME FROM EDU_CLASS_CATEGORY1 WHERE CATEGORY1_KEY = b.CATEGORY1_KEY) AS CATEGORY1_NAME ,(SELECT CATEGORY2_NAME FROM EDU_CLASS_CATEGORY2 WHERE CATEGORY2_KEY = b.CATEGORY2_KEY) AS CATEGORY2_NAME ,(SELECT CATEGORY3_NAME FROM EDU_CLASS_CATEGORY3 WHERE CATEGORY3_KEY = b.CATEGORY3_KEY) AS CATEGORY3_NAME ,DATE_FORMAT(a.REG_DT,'%Y-%m-%d') AS REG_DT, b.INST_NM, b.EDU_TIME ,TIME_FORMAT(SEC_TO_TIME(FLOOR(a.COUR_TIME)),'%i') AS COUR_TIME_MIN ,TIME_FORMAT(SEC_TO_TIME(FLOOR(a.COUR_TIME)),'%s') AS COUR_TIME_SEC ,a.USER_NM, a.COUR_NO, b.EDU_NO ,CASE a.COUR_STAT WHEN 1 THEN '완료' ELSE '미완료' END AS COUR_STAT FROM EDU_COURSE_REGISTRATION a, EDU_REGISTRATION b WHERE a.EDU_NO = b.EDU_NO AND a.USER_ID = #{UserAccount.user_id}
AND TRAIN_S_DATE <= NOW()
AND COUR_STAT = '1'
AND REG_DT = NOW()
AND REG_DT >= #{start_date} AND REG_DT <= #{end_date}
AND b.CATEGORY3_NAME = #{edu_name}
ORDER BY COUR_NO DESC

SELECT  COUR_TIME,COUR_STAT
	FROM EDU_COURSE_REGISTRATION
	 WHERE 1=1
	  AND COUR_NO = #{cour_no}
	  AND EDU_NO = #{edu_no}

mapper.update:

UPDATE EDU_COURSE_REGISTRATION
SET COUR_TIME = #{cour_time}, COUR_STAT = #{cour_stat}
WHERE COUR_NO = #{cour_no}
  AND EDU_NO = #{edu_no}

mapper.select:
SELECT (SELECT CATEGORY1_NAME FROM EDU_CLASS_CATEGORY1 WHERE CATEGORY1_KEY = b.CATEGORY1_KEY) AS CATEGORY1_NAME ,(SELECT CATEGORY2_NAME FROM EDU_CLASS_CATEGORY2 WHERE CATEGORY2_KEY = b.CATEGORY2_KEY) AS CATEGORY2_NAME ,(SELECT CATEGORY3_NAME FROM EDU_CLASS_CATEGORY3 WHERE CATEGORY3_KEY = b.CATEGORY3_KEY) AS CATEGORY3_NAME ,DATE_FORMAT(a.REG_DT,'%Y-%m-%d') AS REG_DT, b.INST_NM ,a.USER_NM, a.COUR_NO ,DATE_FORMAT(a.LICENSE_PBL_DATE,'%Y-%m-%d') AS LICENSE_PBL_DATE ,CASE a.LICENSE_PBL WHEN 1 THEN '발급' ELSE '미발급' END AS LICENSE_PBL FROM EDU_COURSE_REGISTRATION a, EDU_REGISTRATION b WHERE a.EDU_NO = b.EDU_NO AND a.USER_ID = #{UserAccount.user_id}
AND TRAIN_S_DATE <= NOW()
AND COUR_STAT = 1
AND b.CATEGORY1_KEY = #{category1_key}
AND b.CATEGORY2_KEY = #{category2_key}
AND b.CATEGORY3_KEY = #{category3_key}
AND REG_DT = NOW()
AND REG_DT >= #{start_date} AND REG_DT <= #{end_date}
AND LICENSE_PBL = '1'
AND LICENSE_PBL = '2'
ORDER BY COUR_NO DESC LIMIT #{recordCountPerPage} OFFSET #{offset}

SELECT COUNT(*) 
FROM EDU_COURSE_REGISTRATION a, 
	 EDU_REGISTRATION b
WHERE a.EDU_NO = b.EDU_NO
AND a.USER_ID = #{UserAccount.user_id}

AND TRAIN_S_DATE <= NOW() 

AND COUR_STAT = 1

	AND b.CATEGORY1_KEY = #{category1_key}


	AND b.CATEGORY2_KEY = #{category2_key}


	AND b.CATEGORY3_KEY = #{category3_key}





		AND REG_DT = NOW()



       AND  REG_DT >= #{start_date} AND REG_DT <= #{end_date} 







		AND LICENSE_PBL = '1'


      	AND LICENSE_PBL = '2'
