mapper.select:
SELECT  COUR_TIME,COUR_STAT
	FROM EDU_COURSE_REGISTRATION
	 WHERE 1=1
	  AND COUR_NO = #{cour_no}
	  AND EDU_NO = #{edu_no}

mapper.update:

UPDATE EDU_COURSE_REGISTRATION
SET COUR_TIME = #{cour_time}, COUR_STAT = #{cour_stat}
WHERE COUR_NO = #{cour_no}
  AND EDU_NO = #{edu_no}
