    	FILE_KEY = #{board_idx}
    	AND FILE_GUBUN = 'board'

	/**  com.easycompany.mapper.WarrantMapper.selectFile **/	
	SELECT FILE_GUBUN, FILE_ID, FILE_NAME, FILE_FULL_PATH, FILE_SEQ, FILE_ID, FILE_SIZE, FILE_KEY, FILE_OREDER_BY
      FROM edu_file

    	FILE_SEQ = #{file_seq}

