mapper.select:
/**  com.easycompany.mapper.LoginMapper.checkPassword **/	 
SELECT USER_ID ID
      ,USER_NM NAME
      ,PASSWORD
 FROM COM_USER	    
WHERE USER_ID  =  #{id}
  AND PASSWORD =  #{shaPassword}

mapper.insert:
/**  com.easycompany.mapper.LoginMapper.signUpInsert **/	
INSERT 
INTO TB_MEMBER 
(
  	  ID
  	, PASSWORD
  	, SMS_YN
  	, ACCOUNT_YN
 ) 
VALUES 
(
	  #{id}
	, #{shaPassword}
	, #{sms_yn}
	, #{account_yn}
)
